#include "quadrato.h"quadrato::quadrato(int lato){    v=NULL;    v=new int;    *v=lato;//PASSO PARAMETRO DI TIPO INTERO AL LATO AL PUNTATORE}int quadrato::perimetro(int a){  this->a=*(v)*4;return a;}int  quadrato:: area(int b){    this->b=*(v)**(v);    return b;}float  quadrato::diagonale(float c){ this->c=*(v)*sqrt(2); return c;}void quadrato::stampa() {    fprintf(stdout, "  %d        %d        %.2f \n",a, b, c);}void quadrato::file() {    p=fopen( "cronologia.txt", "a");    fprintf(p,"  %d       %d       %.2f  \n", a, b, c);    fclose(p);}